/*
 * This file is part of React-SearchKit.
 * Copyright (C) 2019 CERN.
 *
 * React-SearchKit is free software; you can redistribute it and/or modify it
 * under the terms of the MIT License; see LICENSE file for more details.
 */

import React, { Component } from 'react';
import {
  Segment,
  Container,
  Header,
  Grid,
  Accordion,
  Menu,
} from 'semantic-ui-react';
import {
  ReactSearchKit,
  SearchBar,
  Aggregator,
  EmptyResults,
  Error,
  ResultsLoader,
} from './lib/components';
import { Results } from './Results';
import { InvenioSearchApi } from './lib/api/contrib/invenio';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { activeIndex: -1 };
  }

  handleClick = (e, titleProps) => {
    const { index } = titleProps;
    const { activeIndex } = this.state;
    const newIndex = activeIndex === index ? -1 : index;

    this.setState({ activeIndex: newIndex });
  };

  renderAccordionAggregations = (
    title,
    resultsAggregations,
    aggregations,
    customProps
  ) => {
    const { activeIndex } = this.state;

    return resultsAggregations !== undefined ? (
      <Accordion as={Menu} vertical>
        <Menu.Item>
          <Accordion.Title
            content={title}
            index={customProps.index}
            active={activeIndex === customProps.index}
            onClick={this.handleClick}
          />
          <Accordion.Content
            active={activeIndex === customProps.index}
            content={aggregations}
          />
        </Menu.Item>
      </Accordion>
    ) : null;
  };

  render() {
    const sortValues = [
      {
        text: 'Most viewed',
        sortBy: 'mostviewed',
        sortOrder: 'asc',
        defaultOnEmptyString: true,
      },
      {
        text: 'Least viewed',
        sortBy: 'mostviewed',
        sortOrder: 'desc',
      },
      {
        text: 'Newest',
        sortBy: 'mostrecent',
        sortOrder: 'asc',
        default: true,
      },
      {
        text: 'Oldest',
        sortBy: 'mostrecent',
        sortOrder: 'desc',
      },
    ];

    const resultsPerPageValues = [
      {
        text: '10',
        value: 10,
      },
      {
        text: '20',
        value: 20,
      },
      {
        text: '50',
        value: 50,
      },
    ];

    const searchApi = new InvenioSearchApi({
      url: 'https://zenodo.org/api/records/',
      timeout: 5000,
      headers: { Accept: 'application/vnd.zenodo.v1+json' },
    });

    return (
      <div>
        <ReactSearchKit
          searchApi={searchApi}
          persistentUrl={{ enabled: true }}
          searchOnInit={true}
        >
          <div>
            <Segment inverted>
              <Container>
                <Grid columns="equal" verticalAlign="middle">
                  <Grid.Row>
                    <Grid.Column width={5}>
                      <Header as="h1" content="React-SearchKit" inverted />
                    </Grid.Column>
                    <Grid.Column width={10} textAlign="center">
                      <SearchBar />
                    </Grid.Column>
                  </Grid.Row>
                </Grid>
              </Container>
            </Segment>

            <Container>
              <Grid relaxed style={{ padding: '2em 0' }}>
                <Grid.Row columns={2}>
                  <Grid.Column width={4}>
                    <Aggregator
                      title="File types"
                      field="file_type"
                      customProps={{ index: 0 }}
                      renderElement={this.renderAccordionAggregations}
                    />
                    <br />
                    <Aggregator
                      title="Keywords"
                      field="keywords"
                      customProps={{ index: 1 }}
                      renderElement={this.renderAccordionAggregations}
                    />
                    <br />
                    <Aggregator
                      title="Types"
                      field="type"
                      customProps={{ index: 2 }}
                      renderElement={this.renderAccordionAggregations}
                    />
                    <br />
                  </Grid.Column>
                  <Grid.Column width={12}>
                    <ResultsLoader>
                      <EmptyResults />
                      <Error />
                      <Results
                        resultsPerPageValues={resultsPerPageValues}
                      />
                    </ResultsLoader>
                  </Grid.Column>
                </Grid.Row>
              </Grid>
            </Container>
          </div>
        </ReactSearchKit>
      </div>
    );
  }
}

export default App;
